
public class BitwiseOperationsOnTwoIntegers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		int c=30;
		if(a>5 && b>25) {
			System.out.println("if block");
		}
		else {
			System.out.println("else block");
		}
		if(a>5 || b>25) {
			System.out.println("if block");
		}
		else {
			System.out.println("else block");
		}
		if(a>5^b>25) {
			System.out.println("if block");
		}
		else {
			System.out.println("else block");
		}
	}

}
