

public class UsingFloatAndDoubleDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float f1=10.5f;
		float f2=20.5f;
		float f3;
		float f4;
		double d1=65.35;
		double d2=44.65;
		double d3;
		double d4;
		f3=f1+f2;
		f4=f1-f2;
		d3=d1*d2;
		d4=d1/d2;
		System.out.println(f3);
		System.out.println(f4);
		System.out.println(d3);
		System.out.println(d4);
		
	}

}
