
public class SimpleInterest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float p;//p=principal
		float r;//r=rate per annum
		float t;//t=time(year)
		float si;//simple interest
		p=130000;
		r=12;
		t=2;
		si=(p*r*t)/100;
		System.out.println("Simple interest is :"+si);

	}

}
